import React, { useState } from 'react'
import { farmerMotion } from "framer-motion"
import { CircularProgressbar } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

const Card = (props) => {

    const [expeended , setExpended] = useState(false)

  return (
    <farmerMotion>
        {
            expeended ? (
                "<ExpendedCard/>"
            ):
            <CompactCard params={props}/>
        }
    </farmerMotion>
  )
}

//compactCard

function CompactCard({params}){
    const Png = params.png

    return(
        <div className="CompactCard">
            <div className="redailBar">
                <CircularProgressbar 
                value={params.barValue} 
                text={params.barValue}/>
            </div>
            <div className="details">
                <Png/>
                <span>${params.value}</span>
                <span>Last 24 Hours</span>
            </div>
        </div>
    )

}

export default Card
